	/**
	 * Get a Product Icon by post
	 *
	 * @since 2.0.4
	 *
	 * @param object $post       The product post for which you want product icon returned.
	 * @param string $image_type The type of image you want to get.
	 * @param string $class_name CSS class name.
	 *
	 * @return string               Returns html for the image.
	 */
	public static function get_product_icon( $post, $image_type = 'icon', $class_name = '' ) {

		if ( 'none' === $image_type ) {

			return '';

		}

		if ( 'icon' === $image_type ) {

			$image_id = rstore_get_product_meta( $post->ID, 'imageId' );
			return self::get_icon( $image_id, $class_name );

		}

		return get_the_post_thumbnail( $post->ID, $image_type, self::PRODUCT_IMAGE_SLUG );

	}

	/**
	 * Get a Product Icon by post
	 *
	 * @since 2.0.4
	 *
	 * @param string $image_id   The product post for which you want product icon returned.
	 * @param string $class_name CSS class name.
	 *
	 * @return string            Returns svg html for the image.
	 */
	public static function get_icon( $image_id, $class_name = '' ) {

		$content = sprintf( '<div class="rstore-product-icons %s">', esc_attr( $class_name ) );

		$image_id = strtolower( $image_id );

		switch ( $image_id ) {

			case 'code-signing-certificate':
				$content .= self::CODE_SIGNING_CERTIFICATE;
				break;

			case 'dedicated-ip':
				$content .= self::DEDICATED_IP;
				break;

			case 'dedicated-server':
				$content .= self::DEDICATED_SERVER;
				break;

			case 'domain-registration':
				$content .= self::DOMAIN_REGISTRATION;
				break;

			case 'domain-transfer':
				$content .= self::DOMAIN_TRANSFER;
				break;

			case 'domain-backorder':
				$content .= self::DOMAIN_BACKORDER;
				break;

			case 'domains':
			case '1':
				$content .= self::DOMAINS;
				break;

			case 'email_marketing':
			case 'email-marketing':
				$content .= self::EMAIL_MARKETING;
				break;

			case 'email':
			case '4':
				$content .= self::EMAIL;
				break;

			case 'hosting-cpanel':
				$content .= self::HOSTING_CPANEL;
				break;

			case 'hosting-windows':
				$content .= self::HOSTING_WINDOWS;
				break;

			case 'hosting':
			case '3':
				$content .= self::HOSTING;
				break;

			case 'online-calendar':
				$content .= self::ONLINE_CALENDAR;
				break;

			case 'online-storage':
				$content .= self::ONLINE_STORAGE;
				break;

			case 'online-store':
				$content .= self::ONLINE_STORE;
				break;

			case 'reseller':
				$content .= self::RESELLER_PLAN;
				break;

			case 'sale':
				$content .= self::SALE;
				break;

			case 'seo':
			case '7':
				$content .= self::SEO;
				break;

			case 'ssl':
			case '33':
				$content .= self::SSL;
				break;

			case 'website-backup':
				$content .= self::WEBSITE_BACKUP;
				break;

			case 'websites':
			case 'website-builder':
			case '6':
				$content .= self::WEBSITE_BUILDER;
				break;

			case 'website-security':
				$content .= self::WEBSITE_SECURITY;
				break;

			// @codingStandardsIgnoreStart
			case 'wordpress':
			// @codingStandardsIgnoreEnd
			case '46':
				$content .= self::WORDPRESS;
				break;

			default:
				$content .= self::DEFAULT_PRODUCT;
				break;
		}

		$content .= '</div>';

		/**
		 * Filter Product Icon SVG html.
		 *
		 * @since 2.0.7
		 *
		 * @var string    $content    SVG html for the image.
		 * @var string    $image_id   The product post for which you want product icon returned.
		 * @var string    $class_name CSS class name.
		 */
		$content = apply_filters( 'rstore_icon_html', $content, $image_id, $class_name );

		return $content;

	}

